mapper.select:
SELECT COUNT(ID)
  FROM MEMBER
 WHERE ID = #{id}

SELECT ID, PW, NAME, PHONE, EMAIL, ADDRESS, REGDATE, M_STATE
  FROM MEMBER
 WHERE ID = #{id}
   AND PW = #{pw}

SELECT ID
  FROM MEMBER
 WHERE EMAIL = #{email}

SELECT ID
  FROM MEMBER
 WHERE NAME = #{name}
   AND PHONE = #{phone}

mapper.update:
UPDATE MEMBER
   SET PW = #{pw}
 WHERE EMAIL = #{email}
